(function () {
    class files {
        getInfo() {
            return {
                id: "files",
                name: editor.language["sugarcube.files"],
                color1: "#fcb103",
                color2: "#db9a37",
                color3: "#db8937",
                showColor: true,
                menuIconURI: "data:image/svg+xml;base64,",
                blocks: [
                    {
                        opcode: "setFile",
                        type: sugarcube.BlockType.COMMAND,
                        text: editor.language["sugarcube.files.block.setFile"],
                        arguments: {
                            file: {
                                type: sugarcube.ArgumentType.NUMBER,
                            },
                            content: {
                                type: sugarcube.ArgumentType.NUMBER,
                            },
                        },
                    },
                    {
                        opcode: "getFile",
                        type: sugarcube.BlockType.COMMAND,
                        text: editor.language["sugarcube.files.block.getFile"],
                        arguments: {
                            file: {
                                type: sugarcube.ArgumentType.NUMBER,
                            },
                        },
                    },
                ],
            };
        }
    }

    sugarcube.extensionManager.registerExtension(new files());
})();
